<?phpinclude('config.php');$suc ='1';$er='0';$err='';$id ='';//ajouter intervention dans la page fichecreate.phpif(isset($_POST["fichecreate"])){    $obj = json_decode($_POST["fichecreate"]);    $iduser = $_COOKIE['iduser'];    $datedebut=trim(strip_tags($obj->datedebut));    $datefin=trim(strip_tags($obj->datefin));    $nature=trim(strip_tags($obj->nature));    $prix=trim(strip_tags($obj->prix));    if($nature == "" ){        $err .= '<p>* Veuillez ecrire le titre de votre Conférence.</p><br />';$er = '1';    }	if($prix == "" or !is_numeric($prix) ){        $err .= '<p>* Veuillez spécifier le prix de votre Conférence.</p><br />';$er = '1';    }    $date = str_replace('/', '-', $datedebut);$datedebut = date('Y-m-d H:i:s', strtotime($date));    $date = str_replace('/', '-', $datefin);$datefin = date('Y-m-d H:i:s', strtotime($date));    $datet1 = strtotime($datedebut);    $datet2 = strtotime($datefin);    $hours = round(floatval(($datet2 - $datet1) / 3600),2)   ;    if(intval(strtotime($datedebut))  > intval(strtotime($datefin)) ){        $err .= '<p>* Veuillez choisir l\'heure de debut et l\'heure de fin</p><br />';        $er = '1';    }    if($er == '0'){        $req1=$bdd->prepare('insert into rendezvous(titre,dateStart,dateEnd,idEmetteur,prix) values(?,?,?,?,?)');        $req1->execute(array($nature,$datedebut,$datefin,$iduser,floatval($prix)));        $id = $bdd->lastInsertId();                    }    echo json_encode(array('suc' =>  (string)$suc,'err' =>  (string)$err ,'er' =>  (string)$er,'dta' =>  $id  ));}//reserver une conferenceif(isset($_POST["conference"])){    $obj = json_decode($_POST["conference"]);    $iduser = $_COOKIE['iduser'];    $idconference=trim(strip_tags($obj->idconference));    if(!is_numeric($idconference)){        $err .= '<p>* Veuillez cliquer sur une conférence </p><br />';$er = '1';    }    if($er == '0'){        $req1=$bdd->prepare('insert into reservations(idconference,idc) values(?,?)');        $req1->execute(array($idconference,$iduser));        $id = $bdd->lastInsertId();      }    echo json_encode(array('suc' =>  (string)$suc,'err' =>  (string)$err ,'er' =>  (string)$er,'dta' =>  $id  ));}//supprimer une reservations if(isset($_POST["datav"])){    $obj = json_decode($_POST["datav"]);    $iduser = $_COOKIE['iduser'];    $idreservation=trim(strip_tags($obj->idreservation));    if($er == '0'){		if($_COOKIE['usertype']=='client'){		     $req1=$bdd->prepare('delete from reservations where idreservation = ? and idc=? ');        $req1->execute(array($idreservation,$iduser)); 		}				else{  $req1=$bdd->prepare('delete from reservations where idconference in(select idr from rendezvous where idEmetteur=?) and idreservation=? ');        $req1->execute(array($iduser,$idreservation)); }           }    echo json_encode(array('suc' =>  (string)$suc,'err' =>  (string)$err ,'er' =>  (string)$er ));}