<?phpinclude('config.php');$suc ='1';$er='0';$err='';$duree = 0;$dta ='';//modifier actualiteif(isset($_POST["dtas2"])){    $obj = json_decode($_POST["dtas2"]);    $tab1 = $_POST["tab1"];    $identr = $_COOKIE['iduser'];    $allfiles='';    foreach ($tab1 as $key => $value) {        if ($value !== NULL) {            $allfiles .= '/'.$value;        }}    $atitre=trim(strip_tags($obj->titre));    $slug=trim(strip_tags($obj->slug));    $idff=trim(strip_tags($obj->idf));    $contenu=$obj->contenu;    $categorie=trim(strip_tags($obj->categorie));    if($er == '0'){        if($allfiles!='/'){ $req1=$bdd->prepare('update actualites set atitre=?,allfiles=?,contenu=?,cat=?,slug=? where idf=?');            $req1->execute(array($atitre,$allfiles,$contenu,intval($categorie),$slug,intval($idff)));}        else{ $req1=$bdd->prepare('update actualites set atitre=?,contenu=?,cat=?,slug=? where idf=?');            $req1->execute(array($atitre,$contenu,intval($categorie),$slug,intval($idff)));}        $row = $req1->rowcount();        $id = $bdd->lastInsertId();        if(intval($row) == 0){ $err .= 'l\actualité n\'a pas été modifié veuiller ressayer a nouveau' ; $er =1;}    }    echo json_encode(array('suc' =>  (string)$suc,'err' =>  (string)$err ,'er' =>  (string)$er ));}////////////////////////////////////////ajouter categorieif(isset($_POST["dtavs"])){    $obj = json_decode($_POST["dtavs"]);    $identr = $_COOKIE['iduser'];    $slug=trim(strip_tags($obj->slug));    $contenu=trim(strip_tags($obj->contenu));    if($er == '0'){        $req1=$bdd->prepare('insert into categories(categorie,slug) values(?,?)');        $req1->execute(array($contenu,$slug));        $row = $req1->rowcount();        $id = $bdd->lastInsertId();        if(intval($row) == 0){ $err .= 'la categorie n\'a pas été ajouté veuiller ressayer a nouveau' ; $er =1;}    }    echo json_encode(array('suc' =>  (string)$suc,'err' =>  (string)$err ,'er' =>  (string)$er ));}//modifier la categorieif(isset($_POST["dtavs2"])){    $obj = json_decode($_POST["dtavs2"]);    $identr = $_COOKIE['iduser'];    $slug=trim(strip_tags($obj->slug));    $idcat=intval(trim(strip_tags($obj->idcat)));    $contenu=trim(strip_tags($obj->contenu));    if($er == '0'){        $req1=$bdd->prepare('update categories set categorie=?,slug=? where id_cat=?');        $req1->execute(array($contenu,$slug,$idcat));        $row = $req1->rowcount();        $id = $bdd->lastInsertId();        if(intval($row) == 0){ $err .= 'la categorie n\'a pas été ajouté veuiller ressayer a nouveau' ; $er =1;}    }    echo json_encode(array('suc' =>  (string)$suc,'err' =>  (string)$err ,'er' =>  (string)$er ));}