=$bdd->prepare("select * from packs");$reqx->execute();$resultx = $reqx->fetchAll(\PDO::FETCH_ASSOC);//functions getpack duration function getPack($id){    switch($id){        case '1': return '1m' ; break;        case '2': return '3m'; break;        case '3': return '6m';break;        case '4': return '7d';break;        case '5': return '7d';break;        case '6': return '7d';break;        default: return  '1m';    }}//prix de rendez vousfunction getPrice($id,$rdvtype){    global $bdd;    $req4=$bdd->prepare("select * from packs where idpack=? ");    $req4->execute(array($id));    while($ar = $req4->fetch()){        if($rdvtype == 'visio'){return $ar['prvisio'];}else if($rdvtype == 'public'){return $ar['prpublic'];}else if($rdvtype == 'domicile'){return $ar['prdomicile'];}    }    return 200;}//functionsfunction getPackName($val){        global $bdd;        $req4=$bdd->prepare("select * from packs where idpack=? ");        $req4->execute(array($val));        while($ar = $req4->fetch()){return $ar['packname'];}        return 1;}